import React , {Component} from "react";
class App extends Component{
    constructor(){
        super()
        this.state={
            a:[],b:[],operator:"",result:""
        }
        this.handleOperator=this.handleOperator.bind(this)
        this.handlenumber=this.handlenumber.bind(this)
        this.delete=this.delete.bind(this)
    }

    handlenumber(number){
        if(this.state.operator===""){
            this.setState((prevState)=>({
                a:[...prevState.a,number]
            }))
        }         
        else{
            this.setState((prevState)=>({
                b:[...prevState.b,number]
            }))
        }

        console.log(this.state.a[0])
    }

    handleOperator(op){
        this.setState({operator:op})

    }

    delete(){

    }
    calculate(event) {

    }

    display(){

    }

    render(){
        const button=[]
        const buttonStyle={
            width:"50px",
            height:"50px"
        }
        for(let i=0 ; i<=9 ; i++){
            button.push(<button key={i}style={buttonStyle} onClick={()=>this.handlenumber(i)}> {i} </button>)
        }

        const rows=[]
        for(let i=0 ; i<button.length;i+=3){
            rows.push(
                <div key={i} >
                    {button.slice(i,i+3)}
                </div>
            )
        }
        return(
            <div>
                <input type="text" name="displayScreen" style={{height:"50px",width:"200px"}} value={this.state.result} readOnly></input><br/>
               {rows}
               <br/>
               <button style={buttonStyle} onClick={()=>this.handleOperator("+")} > + </button> 
               <button style={buttonStyle} onClick={()=>this.handleOperator("-")} > - </button> 
               <button style={buttonStyle} onClick={()=>this.handleOperator("*")} > * </button> 
               <button style={buttonStyle} onClick={()=>this.handleOperator("/")} > / </button> 
               <br/>
               <button style={{width:"70px", height:"40px"}} onClick={this.delete} > del </button>
               <button style={{width:"130px", height:"40px"}} onClick={this.calculate} > = </button>
            </div>
        )
    }
}
export default App
