import React , {Component} from "react";
import ReactDOM from 'react-dom'
class App extends Component{
    constructor(){
        super()
        this.state={
            a:"",b:"",operator:""
        }
    }

    calculate(event) {

    }

    render(){
        const button=[]
        const buttonStyle={
            width:"50px",
            height:"50px"
        }
        for(let i=0 ; i<=9 ; i++){
            button.push(<button key={i}style={buttonStyle}> {i} </button>)
        }

        const rows=[]
        for(let i=0 ; i<button.length;i+=3){
            rows.push(
                <div key={i} >
                    {button.slice(i,i+3)}
                </div>
            )
        }
        return(
            <div>
                <input type="text" name="displayScreen" style={{height:"50px"}}></input><br/>
               {rows}
               <br/>
               <button style={buttonStyle}>+</button> 
               <button style={buttonStyle}>-</button> 
               <button style={buttonStyle}>*</button> 
               <button style={buttonStyle}>/</button> 
            </div>
        )
    }
}
export default App
