import React  , {Component} from "react";

class MemeGenerator extends Component{
    super(){
        this.state={
            topText:"",
            bottomText:"",
            randomImg:"http://i.imgflip.com/1bij.jpg",
            allMemeImgs:[]
        }
    }
    componentDidMount(){
        fetch("https://api.imgflip.com/get_memes")
            .then(response=>response.json())

            .then(response=>{
                const {memes}=response.data
                this.setState({allMemeImgs:memes})
            })
    }

    handleChange(event){
        const {name,value}=event.target
        this.setState({
            [name]:value
        })
    }
    render(){
        return(
             <div>
                <form className="meme_form"> 
                        <input type="text" name="topText"value={this.state.topText} onChange={this.handleChange()}></input>
                        <input type="text" name="bottomText"value={this.state.bottomTextText} onChange={this.handleChange()}></input>
                        <button>Submit</button>
                </form>
             </div>
        )
    }
}

export default MemeGenerator;
